var db *gorm.DB // ประกาศตัวแปร db เป็นชนิดของตัวชี้ (pointer) ไปยัง GORM database

func DB() *gorm.DB {
	return db
}

type CustomLogger struct{}

func (l *CustomLogger) LogMode(level logger.LogLevel) logger.Interface {
	return l
}

func (l *CustomLogger) Info(ctx context.Context, msg string, args ...interface{}) {}

func (l *CustomLogger) Warn(ctx context.Context, msg string, args ...interface{}) {}

func (l *CustomLogger) Error(ctx context.Context, msg string, args ...interface{}) {
	if !strings.Contains(msg, "record not found") {
		log.Printf(msg, args...)
	}
}

func (l *CustomLogger) Trace(ctx context.Context, begin time.Time, fc func() (string, int64), err error) {
}

func ConnectionDB() {
	database, err := gorm.Open(sqlite.Open("reviews.db?cache=shared"), &gorm.Config{
		Logger: &CustomLogger{}, // ใช้ Custom Logger
	})
	if err != nil {
		panic("failed to connect database")
	}
	fmt.Println("connected database")
	db = database
}

